select get_json_object(hive_json.json, '$.User.Name'),concat(get_json_object(hive_json.json, '$.User.ShippingAddress.Address1'),',',get_json_object(hive_json.json, '$.User.ShippingAddress.Address2'),',',get_json_object(hive_json.json,'$.User.ShippingAddress.City'),',',get_json_object(hive_json.json, '$.User.ShippingAddress.State'),', ',get_json_object(hive_json.json, '$.User.ShippingAddress.PostalCode')) from hive_json where get_json_object(hive_json.json, '$.User.Name') similar to '%(lley)%';
